import { useState, useEffect, useRef, useMemo } from 'react';
import axios from 'axios';

function CreateQuiz({ navigate, editingQuizId }) {
  const defaultQuizState = useMemo(() => ({ title: '', questions: [] }), []);
  const [quiz, setQuiz] = useState(defaultQuizState);
  const [question, setQuestion] = useState({
    text: '',
    options: ['', '', '', ''],
    correctAnswer: ''
  });
  const [error, setError] = useState('');
  const editingRef = useRef(editingQuizId);

  // Debug: Log props and state
  console.log('CreateQuiz render - editingQuizId:', editingQuizId, 'quiz:', quiz);

  useEffect(() => {
    // Skip reset if editingQuizId hasn't changed
    if (editingRef.current === editingQuizId && quiz.questions.length > 0) return;

    editingRef.current = editingQuizId;
    console.log('useEffect triggered - editingQuizId:', editingQuizId);

    // Reset state
    setQuiz(defaultQuizState);
    setQuestion({ text: '', options: ['', '', '', ''], correctAnswer: '' });
    setError('');

    if (editingQuizId) {
      axios.get(`/api/quizzes/${editingQuizId}`)
        .then((response) => {
          if (editingRef.current !== editingQuizId) return;
          const data = response.data;
          if (!data || !data.title || !Array.isArray(data.questions)) {
            throw new Error('Invalid quiz data: title or questions missing');
          }
          console.log('Fetched quiz:', data);
          setQuiz({
            title: data.title || '',
            questions: Array.isArray(data.questions) ? data.questions : []
          });
          setError('');
        })
        .catch((err) => {
          if (editingRef.current !== editingQuizId) return;
          setError('Failed to load quiz for editing. Please try again.');
          console.error('Error fetching quiz:', err.message, err.response?.data);
        });
    }
  }, [editingQuizId, defaultQuizState]);

  const handleAddQuestion = () => {
    const { text, options, correctAnswer } = question;
    if (!text.trim() || !options.every((opt) => opt.trim()) || !correctAnswer.trim()) {
      setError('Please fill in all fields.');
      return;
    }
    if (!options.includes(correctAnswer)) {
      setError('Correct answer must be one of the options.');
      return;
    }
    console.log('Adding question to quiz:', quiz);
    setQuiz((prev) => ({
      ...prev,
      questions: Array.isArray(prev.questions) ? [...prev.questions, { text, options, correctAnswer }] : [{ text, options, correctAnswer }]
    }));
    setQuestion({ text: '', options: ['', '', '', ''], correctAnswer: '' });
    setError('');
    alert('Question added!');
  };

  const handleSubmit = async () => {
    console.log('Submitting quiz:', quiz);
    if (!quiz || typeof quiz !== 'object' || !quiz.title.trim() || !Array.isArray(quiz.questions) || quiz.questions.length === 0) {
      setError('Please provide a title and at least one question.');
      console.error('Invalid quiz state on submit:', quiz);
      return;
    }
    try {
      const url = editingQuizId ? `/api/quizzes/${editingQuizId}` : '/api/quizzes';
      const method = editingQuizId ? 'put' : 'post';
      const response = await axios[method](url, quiz);
      console.log('Quiz saved:', response.data);
      navigate('quizList');
      setError('');
    } catch (err) {
      setError('Failed to save quiz. Please ensure the backend is running.');
      console.error('Error saving quiz:', err.message, err.response?.data);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">{editingQuizId ? 'Edit Quiz' : 'Create Quiz'}</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="quizTitle">Quiz Title</label>
        <input
          id="quizTitle"
          type="text"
          value={quiz.title}
          onChange={(e) => setQuiz((prev) => {
            console.log('Updating title, current quiz:', prev);
            return { ...prev, title: e.target.value, questions: Array.isArray(prev.questions) ? prev.questions : [] };
          })}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter quiz title"
          aria-required="true"
        />
      </div>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">Add Question</h3>
      <div className="mb-4">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="questionText">Question Text</label>
        <input
          id="questionText"
          type="text"
          value={question.text}
          onChange={(e) => setQuestion({ ...question, text: e.target.value })}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter question text"
          aria-required="true"
        />
      </div>
      {['Option 1', 'Option 2', 'Option 3', 'Option 4'].map((label, i) => (
        <div key={i} className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor={`option${i}`}>{label}</label>
          <input
            id={`option${i}`}
            type="text"
            value={question.options[i]}
            onChange={(e) => {
              const newOptions = [...question.options];
              newOptions[i] = e.target.value;
              setQuestion({ ...question, options: newOptions });
            }}
            className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
            placeholder={`Enter option ${i + 1}`}
            aria-required="true"
          />
        </div>
      ))}
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="correctAnswer">Correct Answer</label>
        <input
          id="correctAnswer"
          type="text"
          value={question.correctAnswer}
          onChange={(e) => setQuestion({ ...question, correctAnswer: e.target.value })}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter correct answer"
          aria-required="true"
        />
      </div>
      <div className="flex gap-4 flex-wrap justify-center">
        <button
          className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-600 hover:shadow-lg transform hover:-translate-y-1 transition"
          onClick={handleAddQuestion}
        >
          Add Question
        </button>
        <button
          className={`bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition ${!quiz.title.trim() || !Array.isArray(quiz.questions) || quiz.questions.length === 0 ? 'opacity-50 cursor-not-allowed' : ''}`}
          onClick={handleSubmit}
          disabled={!quiz.title.trim() || !Array.isArray(quiz.questions) || quiz.questions.length === 0}
        >
          Submit Quiz
        </button>
        <button
          className="bg-red-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-red-600 hover:shadow-lg transform hover:-translate-y-1 transition"
          onClick={() => navigate('quizList')}
        >
          Cancel
        </button>
      </div>
    </div>
  );
}

export default CreateQuiz;
