function Results({ navigate, quiz, userAnswers = [] }) {
  // Debug: Log props
  console.log('Results render - quiz:', quiz, 'userAnswers:', userAnswers);

  // Check if quiz or userAnswers is invalid
  if (!quiz || !quiz.title || !Array.isArray(quiz.questions) || quiz.questions.length === 0) {
    return (
      <div className="bg-white bg-opacity-70 backdrop-blur-md rounded-lg p-8 shadow-xl outline outline-1 outline-gray-200 max-w-2xl mx-auto text-center animate-fade-in">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Error</h2>
        <p className="text-red-500 mb-4">Quiz not found or invalid. Please select a valid quiz.</p>
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition-all duration-300"
          onClick={() => navigate('/quizList')}
        >
          Back to Quiz List
        </button>
      </div>
    );
  }

  // Calculate score
  const score = userAnswers.reduce((acc, answer) => {
    return answer.selectedAnswer && answer.selectedAnswer === answer.correctAnswer ? acc + 1 : acc;
  }, 0);

  return (
    <div className="bg-white bg-opacity-70 backdrop-blur-md rounded-lg p-8 shadow-xl outline outline-1 outline-gray-200 max-w-2xl mx-auto animate-fade-in">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">{quiz.title} - Results</h2>
      <p className="text-xl font-semibold text-gray-800 mb-4">
        Your Score: {score} / {quiz.questions.length}
      </p>
      <div className="flex flex-col gap-4 mb-6">
        {userAnswers.map((answer, index) => (
          <div key={index} className="border border-gray-300 rounded-lg p-4">
            <p className="text-gray-800 font-medium">Question {index + 1}: {answer.question}</p>
            <p className={`text-gray-600 ${answer.selectedAnswer ? '' : 'text-red-500'}`}>
              Your Answer: {answer.selectedAnswer || 'Not answered'}
            </p>
            <p className="text-gray-600">
              Correct Answer: {answer.correctAnswer}
            </p>
            <p className={answer.selectedAnswer === answer.correctAnswer ? 'text-green-500' : 'text-red-500'}>
              {answer.selectedAnswer === answer.correctAnswer ? 'Correct' : 'Incorrect'}
            </p>
          </div>
        ))}
      </div>
      <div className="flex gap-4 justify-center">
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition-all duration-300"
          onClick={() => navigate('/quizList')}
        >
          Back to Quiz List
        </button>
      </div>
    </div>
  );
}

export default Results;
