function Home({ navigate }) {
  return (
    <div className="text-center">
      <h1 className="text-4xl font-extrabold text-blue-800 mb-6">Welcome to QuizMaster AI</h1>
      <p className="text-lg text-gray-600 mb-8">Test your knowledge with interactive quizzes!</p>
      <div className="flex justify-center gap-4 flex-wrap">
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition"
          onClick={() => navigate('createQuiz')}
          aria-label="Create a new quiz"
        >
          Create Quiz
        </button>
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition"
          onClick={() => navigate('quizList')}
          aria-label="View available quizzes"
        >
          View Quizzes
        </button>
      </div>
    </div>
  );
}

export default Home;
