import { useState } from 'react';
import Home from './components/Home';
import QuizList from './components/QuizList';
import CreateQuiz from './components/CreateQuiz';
import TakeQuiz from './components/TakeQuiz';
import Results from './components/Results';

function App() {
  const [section, setSection] = useState('home');
  const [currentQuiz, setCurrentQuiz] = useState(null);
  const [userAnswers, setUserAnswers] = useState([]);
  const [editingQuizId, setEditingQuizId] = useState(null);

  const showSection = (newSection, quiz = null, answers = []) => {
    setSection(newSection);
    setCurrentQuiz(quiz);
    setUserAnswers(answers);
  };

  return (
    <div className="container animate-fadeIn">
      {section === 'home' && <Home showSection={showSection} />}
      {section === 'main' && <QuizList showSection={showSection} setEditingQuizId={setEditingQuizId} />}
      {section === 'createQuiz' && <CreateQuiz showSection={showSection} editingQuizId={editingQuizId} />}
      {section === 'takeQuiz' && <TakeQuiz showSection={showSection} currentQuiz={currentQuiz} userAnswers={userAnswers} />}
      {section === 'results' && <Results showSection={showSection} currentQuiz={currentQuiz} userAnswers={userAnswers} />}
    </div>
  );
}

export default App;
