import { useState, useEffect } from 'react';

function CreateQuiz({ showSection, editingQuizId }) {
  // Initialize quiz with questions as an empty array
  const [quiz, setQuiz] = useState({ title: '', questions: [] });
  const [question, setQuestion] = useState({
    text: '',
    options: ['', '', '', ''],
    correctAnswer: ''
  });
  const [error, setError] = useState('');

  useEffect(() => {
    if (editingQuizId) {
      fetch(`http://localhost:3000/api/quizzes/${editingQuizId}`)
        .then((res) => {
          if (!res.ok) throw new Error('Failed to fetch quiz');
          return res.json();
        })
        .then((data) => {
          // Ensure questions is an array
          setQuiz({ title: data.title || '', questions: Array.isArray(data.questions) ? data.questions : [] });
        })
        .catch((error) => {
          console.error('Error fetching quiz:', error);
          setError('Failed to load quiz for editing. Please try again.');
        });
    } else {
      // Reset state for new quiz
      setQuiz({ title: '', questions: [] });
      setError('');
    }
  }, [editingQuizId]);

  const handleAddQuestion = () => {
    const { text, options, correctAnswer } = question;
    // Validate inputs
    if (!text || !options.every((opt) => opt) || !correctAnswer) {
      setError('Please fill in all fields.');
      return;
    }
    if (!options.includes(correctAnswer)) {
      setError('Correct answer must match one of the options.');
      return;
    }
    setQuiz({ ...quiz, questions: [...quiz.questions, { text, options, correctAnswer }] });
    setQuestion({ text: '', options: ['', '', '', ''], correctAnswer: '' });
    setError('');
    alert('Question added!');
  };

  const handleSubmitQuiz = async () => {
    if (!quiz.title || !quiz.questions || quiz.questions.length === 0) {
      setError('Please add a title and at least one question.');
      return;
    }
    try {
      const response = await fetch(
        editingQuizId ? `http://localhost:3000/api/quizzes/${editingQuizId}` : 'http://localhost:3000/api/quizzes',
        {
          method: editingQuizId ? 'PUT' : 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(quiz),
        }
      );
      if (!response.ok) throw new Error('Failed to save quiz');
      showSection('main');
      setError('');
    } catch (error) {
      console.error('Error saving quiz:', error);
      setError('Failed to save quiz. Please ensure the backend server is running and try again.');
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">{editingQuizId ? 'Edit Quiz' : 'Create Quiz'}</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="quizTitle">Quiz Title</label>
        <input
          id="quizTitle"
          type="text"
          value={quiz.title}
          onChange={(e) => setQuiz({ ...quiz, title: e.target.value.trim() })}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter quiz title"
          aria-required="true"
        />
      </div>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">Add Question</h3>
      <div className="mb-4">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="questionText">Question Text</label>
        <input
          id="questionText"
          type="text"
          value={question.text}
          onChange={(e) => setQuestion({ ...question, text: e.target.value.trim() })}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter question text"
          aria-required="true"
        />
      </div>
      {['Option 1', 'Option 2', 'Option 3', 'Option 4'].map((label, i) => (
        <div key={i} className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor={`option${i}`}>{label}</label>
          <input
            id={`option${i}`}
            type="text"
            value={question.options[i]}
            onChange={(e) => {
              const newOptions = [...question.options];
              newOptions[i] = e.target.value.trim();
              setQuestion({ ...question, options: newOptions });
            }}
            className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
            placeholder={`Enter option ${i + 1}`}
            aria-required="true"
          />
        </div>
      ))}
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="correctAnswer">Correct Answer</label>
        <input
          id="correctAnswer"
          type="text"
          value={question.correctAnswer}
          onChange={(e) => setQuestion({ ...question, correctAnswer: e.target.value.trim() })}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter correct answer"
          aria-required="true"
        />
      </div>
      <div className="flex gap-4 flex-wrap justify-center">
        <button
          className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-600 transform hover:-translate-y-1 hover:shadow-lg transition"
          onClick={handleAddQuestion}
        >
          Add Question
        </button>
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 transform hover:-translate-y-1 hover:shadow-lg transition"
          onClick={handleSubmitQuiz}
        >
          Submit Quiz
        </button>
        <button
          className="bg-red-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-red-600 transform hover:-translate-y-1 hover:shadow-lg transition"
          onClick={() => showSection('main')}
        >
          Cancel
        </button>
      </div>
    </div>
  );
}

export default CreateQuiz;
