import { useState, useEffect } from 'react';

function QuizList({ showSection, setEditingQuizId }) {
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    fetchQuizzes();
  }, []);

  const fetchQuizzes = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/quizzes');
      if (!response.ok) throw new Error('Failed to fetch quizzes');
      setQuizzes(await response.json());
    } catch (error) {
      console.error('Error fetching quizzes:', error);
      alert('Failed to load quizzes. Please try again.');
    }
  };

  const handleDeleteQuiz = async (quizId) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      try {
        const response = await fetch(`http://localhost:3000/api/quizzes/${quizId}`, {
          method: 'DELETE',
        });
        if (!response.ok) throw new Error('Failed to delete quiz');
        fetchQuizzes();
      } catch (error) {
        console.error('Error deleting quiz:', error);
        alert('Failed to delete quiz. Please try again.');
      }
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Quiz Platform</h2>
      <button
        className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 transform hover:-translate-y-1 hover:shadow-lg transition mb-6"
        aria-label="Create a new quiz"
        onClick={() => {
          setEditingQuizId(null);
          showSection('createQuiz');
        }}
      >
        Create New Quiz
      </button>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">Available Quizzes</h3>
      <ul className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {quizzes.map((quiz) => (
          <li key={quiz._id} className="bg-gray-50 p-4 rounded-lg shadow hover:shadow-lg transform hover:-translate-y-1 transition">
            <a
              href="#"
              className="text-blue-600 font-medium hover:underline"
              onClick={(e) => {
                e.preventDefault();
                showSection('takeQuiz', quiz, new Array(quiz.questions.length).fill(''));
              }}
            >
              {quiz.title}
            </a>
            <div className="flex gap-4 mt-4">
              <button
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transform hover:-translate-y-1 transition"
                aria-label={`Edit ${quiz.title}`}
                onClick={() => {
                  setEditingQuizId(quiz._id);
                  showSection('createQuiz', quiz);
                }}
              >
                Edit
              </button>
              <button
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transform hover:-translate-y-1 transition"
                aria-label={`Delete ${quiz.title}`}
                onClick={() => handleDeleteQuiz(quiz._id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default QuizList;
