function Home({ showSection }) {
  return (
    <div className="text-center">
      <h1 className="text-4xl font-extrabold text-blue-800 mb-6">Welcome to QuizMaster AI</h1>
      <p className="text-lg text-gray-600 mb-8">Unleash your knowledge with fun and interactive quizzes!</p>
      <div className="flex justify-center gap-4 flex-wrap">
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 transform hover:-translate-y-1 hover:shadow-lg transition"
          aria-label="Create a new quiz"
          onClick={() => showSection('createQuiz')}
        >
          Create a Quiz
        </button>
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 transform hover:-translate-y-1 hover:shadow-lg transition"
          aria-label="View available quizzes"
          onClick={() => showSection('main')}
        >
          View Quizzes
        </button>
      </div>
    </div>
  );
}

export default Home;
