import { useState } from 'react';
import Home from './components/Home';
import QuizList from './components/QuizList';
import CreateQuiz from './components/CreateQuiz';
import TakeQuiz from './components/TakeQuiz';
import Results from './components/Results';

function App() {
  const [section, setSection] = useState('home');
  const [currentQuiz, setCurrentQuiz] = useState(null);
  const [userAnswers, setUserAnswers] = useState([]);
  const [editingQuizId, setEditingQuizId] = useState(null);

  const navigate = (newSection, quiz = null, answers = []) => {
    console.log('Navigating to:', newSection, 'with quiz:', quiz, 'editingQuizId:', quiz?._id);
    setSection(newSection);
    setCurrentQuiz(quiz);
    setUserAnswers(answers);
    setEditingQuizId(newSection === 'createQuiz' && quiz && quiz._id ? quiz._id : null);
  };

  return (
    <div className="container animate-fadeIn">
      {section === 'home' && <Home navigate={navigate} />}
      {section === 'quizList' && <QuizList navigate={navigate} />}
      {section === 'createQuiz' && <CreateQuiz navigate={navigate} editingQuizId={editingQuizId} />}
      {section === 'takeQuiz' && <TakeQuiz navigate={navigate} quiz={currentQuiz} userAnswers={userAnswers} />}
      {section === 'results' && <Results navigate={navigate} quiz={currentQuiz} userAnswers={userAnswers} />}
    </div>
  );
}

export default App;
