function Results({ navigate, quiz, userAnswers }) {
  const score = quiz.questions.reduce((count, q, i) => count + (q.correctAnswer === userAnswers[i] ? 1 : 0), 0);

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Your Score: {score} / {quiz.questions.length}</h2>
      <div className="space-y-4">
        {quiz.questions.map((q, i) => (
          <div key={i} className={`result-card ${userAnswers[i] === q.correctAnswer ? 'correct' : 'incorrect'}`}>
            <p className="text-lg font-semibold text-gray-800 mb-2">{q.text}</p>
            <p>Your Answer: {userAnswers[i] || 'Not answered'}</p>
            <p>Correct Answer: {q.correctAnswer}</p>
          </div>
        ))}
      </div>
      <button
        className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition mt-6"
        onClick={() => navigate('quizList')}
      >
        Back to Quizzes
      </button>
    </div>
  );
}

export default Results;
