import { useState } from 'react';

function TakeQuiz({ showSection, currentQuiz, userAnswers }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState(userAnswers);

  const handleAnswerChange = (value) => {
    const newAnswers = [...answers];
    newAnswers[currentIndex] = value;
    setAnswers(newAnswers);
  };

  const handleNext = () => {
    if (currentIndex < currentQuiz.questions.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleSubmit = () => {
    showSection('results', currentQuiz, answers);
  };

  const question = currentQuiz.questions[currentIndex];

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">{currentQuiz.title}</h2>
      <div className="text-center text-gray-600 mb-4">
        Question {currentIndex + 1} of {currentQuiz.questions.length}
      </div>
      <div className="question-card">
        <p className="text-lg font-semibold text-gray-800 mb-4">{question.text}</p>
        {question.options.map((option, i) => (
          <label key={i} className="block mb-2 text-gray-600">
            <input
              type="radio"
              name={`question-${currentIndex}`}
              value={option}
              checked={answers[currentIndex] === option}
              onChange={() => handleAnswerChange(option)}
              className="mr-2"
            />
            {option}
          </label>
        ))}
      </div>
      <div className="flex gap-4 flex-wrap justify-center">
        <button
          className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-600 transform hover:-translate-y-1 hover:shadow-lg transition"
          onClick={handlePrevious}
          disabled={currentIndex === 0}
        >
          Previous
        </button>
        {currentIndex < currentQuiz.questions.length - 1 ? (
          <button
            className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-600 transform hover:-translate-y-1 hover:shadow-lg transition"
            onClick={handleNext}
          >
            Next
          </button>
        ) : (
          <button
            className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 transform hover:-translate-y-1 hover:shadow-lg transition"
            onClick={handleSubmit}
          >
            Submit Answers
          </button>
        )}
      </div>
    </div>
  );
}

export default TakeQuiz;
