import { useState } from 'react';
import Home from './components/Home';
import QuizList from './components/QuizList';
import CreateQuiz from './components/CreateQuiz';
import TakeQuiz from './components/TakeQuiz';
import Results from './components/Results';

function App() {
  const [section, setSection] = useState('home');
  const [currentQuiz, setCurrentQuiz] = useState(null);
  const [userAnswers, setUserAnswers] = useState([]);
  const [editingQuizId, setEditingQuizId] = useState(null);

  const navigate = (newSection, quiz = null, answers = []) => {
    const newEditingQuizId = newSection === 'createQuiz' && quiz && typeof quiz._id === 'string' && quiz._id.length === 24 ? quiz._id : null;
    console.log('Navigating to:', newSection, 'with quiz:', quiz, 'editingQuizId:', newEditingQuizId);
    setSection(newSection);
    setCurrentQuiz(quiz);
    setUserAnswers(answers);
    setEditingQuizId(newEditingQuizId);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-100 flex flex-col">
      <header className="fixed top-0 left-0 right-0 bg-blue-800 bg-opacity-80 backdrop-blur-sm p-4 flex justify-between items-center shadow-lg z-10 animate-slide-down">
        <h1 className="text-2xl font-bold text-white">Quiz Maker</h1>
        <nav className="flex gap-4">
          <button
            className="text-white hover:text-blue-200 transition-transform duration-300 hover:scale-110"
            onClick={() => navigate('home')}
          >
            Home
          </button>
          <button
            className="text-white hover:text-blue-200 transition-transform duration-300 hover:scale-110"
            onClick={() => navigate('quizList')}
          >
            Quiz List
          </button>
          <button
            className="text-white hover:text-blue-200 transition-transform duration-300 hover:scale-110"
            onClick={() => navigate('createQuiz')}
          >
            Create Quiz
          </button>
        </nav>
      </header>
      <main className="flex-grow flex items-center justify-center">
        <div className="container max-w-4xl mx-auto p-4">
          {section === 'home' && <Home navigate={navigate} />}
          {section === 'quizList' && <QuizList navigate={navigate} />}
          {section === 'createQuiz' && <CreateQuiz navigate={navigate} editingQuizId={editingQuizId} />}
          {section === 'takeQuiz' && <TakeQuiz navigate={navigate} quiz={currentQuiz} userAnswers={userAnswers} />}
          {section === 'results' && <Results navigate={navigate} quiz={currentQuiz} userAnswers={userAnswers} />}
        </div>
      </main>
      <footer className="bg-blue-800 bg-opacity-80 backdrop-blur-sm p-4 text-center text-white shadow-inner animate-slide-up">
        <p>&copy; 2025 Shivraj. All rights reserved. | <a href="mailto:support@quizmaker.com" className="hover:text-blue-200 transition-colors">Contact Us</a></p>
      </footer>
    </div>
  );
}

export default App;
