import { useState, useEffect } from 'react';
import axios from 'axios';

function CreateQuiz({ navigate, editingQuizId }) {
  const [formData, setFormData] = useState({
    title: '',
    questions: [],
    newQuestion: { text: '', options: ['', '', '', ''], correctAnswer: '' },
    isLoading: false
  });
  const [error, setError] = useState('');

  // Utility to ensure questions is an array
  const ensureQuestionsArray = (questions) => Array.isArray(questions) ? questions : [];

  // Debug: Log state and props
  console.log('CreateQuiz render - editingQuizId:', editingQuizId, 'formData:', formData);

  useEffect(() => {
    if (!editingQuizId) {
      setFormData({
        title: '',
        questions: [],
        newQuestion: { text: '', options: ['', '', '', ''], correctAnswer: '' },
        isLoading: false
      });
      return;
    }

    setFormData((prev) => ({ ...prev, isLoading: true }));
    axios.get(`/api/quizzes/${editingQuizId}`)
      .then((response) => {
        const data = response.data;
        console.log('Fetched quiz:', data);
        if (!data || !data.title || !Array.isArray(data.questions)) {
          setError('Invalid quiz data received.');
          setFormData((prev) => ({ ...prev, isLoading: false }));
          return;
        }
        setFormData({
          title: data.title || '',
          questions: ensureQuestionsArray(data.questions),
          newQuestion: { text: '', options: ['', '', '', ''], correctAnswer: '' },
          isLoading: false
        });
        setError('');
      })
      .catch((err) => {
        setError('Failed to load quiz. Please try again.');
        console.error('Error fetching quiz:', err.message, err.response?.data);
        setFormData((prev) => ({ ...prev, isLoading: false }));
      });
  }, [editingQuizId]);

  const handleAddQuestion = () => {
    const { text, options, correctAnswer } = formData.newQuestion;
    if (!text.trim() || !options.every((opt) => opt.trim()) || !correctAnswer.trim()) {
      setError('Please fill in all fields.');
      return;
    }
    if (!options.includes(correctAnswer)) {
      setError('Correct answer must be one of the options.');
      return;
    }
    setFormData((prev) => ({
      ...prev,
      questions: ensureQuestionsArray(prev.questions).concat({ text, options, correctAnswer }),
      newQuestion: { text: '', options: ['', '', '', ''], correctAnswer: '' },
      error: ''
    }));
    setError('');
    alert('Question added!');
  };

  const handleSubmit = async () => {
    console.log('Submitting formData:', formData);
    if (!formData.title.trim() || !ensureQuestionsArray(formData.questions).length) {
      setError('Please provide a title and at least one question.');
      console.error('Invalid formData on submit:', formData);
      return;
    }
    try {
      const payload = { title: formData.title, questions: formData.questions };
      const url = editingQuizId ? `/api/quizzes/${editingQuizId}` : '/api/quizzes';
      const method = editingQuizId ? 'put' : 'post';
      const response = await axios[method](url, payload);
      console.log('Quiz saved:', response.data);
      navigate('quizList');
      setError('');
    } catch (err) {
      setError('Failed to save quiz. Please ensure the backend is running.');
      console.error('Error saving quiz:', err.message, err.response?.data);
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">{editingQuizId ? 'Edit Quiz' : 'Create Quiz'}</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {formData.isLoading && <p className="text-gray-500 mb-4">Loading quiz...</p>}
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="quizTitle">Quiz Title</label>
        <input
          id="quizTitle"
          type="text"
          value={formData.title}
          onChange={(e) => setFormData((prev) => ({ ...prev, title: e.target.value }))}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter quiz title"
          aria-required="true"
          disabled={formData.isLoading}
        />
      </div>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">Add Question</h3>
      <div className="mb-4">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="questionText">Question Text</label>
        <input
          id="questionText"
          type="text"
          value={formData.newQuestion.text}
          onChange={(e) => setFormData((prev) => ({
            ...prev,
            newQuestion: { ...prev.newQuestion, text: e.target.value }
          }))}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter question text"
          aria-required="true"
          disabled={formData.isLoading}
        />
      </div>
      {['Option 1', 'Option 2', 'Option 3', 'Option 4'].map((label, i) => (
        <div key={i} className="mb-4">
          <label className="block text-gray-700 font-medium mb-2" htmlFor={`option${i}`}>{label}</label>
          <input
            id={`option${i}`}
            type="text"
            value={formData.newQuestion.options[i]}
            onChange={(e) => setFormData((prev) => {
              const newOptions = [...prev.newQuestion.options];
              newOptions[i] = e.target.value;
              return { ...prev, newQuestion: { ...prev.newQuestion, options: newOptions } };
            })}
            className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
            placeholder={`Enter option ${i + 1}`}
            aria-required="true"
            disabled={formData.isLoading}
          />
        </div>
      ))}
      <div className="mb-6">
        <label className="block text-gray-700 font-medium mb-2" htmlFor="correctAnswer">Correct Answer</label>
        <input
          id="correctAnswer"
          type="text"
          value={formData.newQuestion.correctAnswer}
          onChange={(e) => setFormData((prev) => ({
            ...prev,
            newQuestion: { ...prev.newQuestion, correctAnswer: e.target.value }
          }))}
          className="w-full p-3 border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
          placeholder="Enter correct answer"
          aria-required="true"
          disabled={formData.isLoading}
        />
      </div>
      <div className="flex gap-4 flex-wrap justify-center">
        <button
          className="bg-green-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-600 hover:shadow-lg transform hover:-translate-y-1 transition disabled:opacity-50 disabled:cursor-not-allowed"
          onClick={handleAddQuestion}
          disabled={formData.isLoading}
        >
          Add Question
        </button>
        <button
          className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition disabled:opacity-50 disabled:cursor-not-allowed"
          onClick={handleSubmit}
          disabled={formData.isLoading || !formData.title.trim() || !ensureQuestionsArray(formData.questions).length}
        >
          Submit Quiz
        </button>
        <button
          className="bg-red-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-red-600 hover:shadow-lg transform hover:-translate-y-1 transition"
          onClick={() => navigate('quizList')}
          disabled={formData.isLoading}
        >
          Cancel
        </button>
      </div>
    </div>
  );
}

export default CreateQuiz;
