import { useState, useEffect } from 'react';
import axios from 'axios';

function QuizList({ navigate }) {
  const [quizzes, setQuizzes] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const response = await axios.get('/api/quizzes');
        setQuizzes(response.data);
        setError('');
      } catch (err) {
        setError('Failed to load quizzes. Please ensure the backend is running.');
        console.error('Error fetching quizzes:', err);
      }
    };
    fetchQuizzes();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      try {
        await axios.delete(`/api/quizzes/${id}`);
        setQuizzes(quizzes.filter((quiz) => quiz._id !== id));
        setError('');
      } catch (err) {
        setError('Failed to delete quiz. Please try again.');
        console.error('Error deleting quiz:', err);
      }
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Quiz List</h2>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <button
        className="bg-blue-500 text-white px-6 py-3 rounded-lg font-semibold hover:bg-blue-600 hover:shadow-lg transform hover:-translate-y-1 transition mb-6"
        onClick={() => navigate('createQuiz')}
        aria-label="Create a new quiz"
      >
        Create New Quiz
      </button>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">Available Quizzes</h3>
      <ul className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {quizzes.map((quiz) => (
          <li key={quiz._id} className="bg-gray-50 p-4 rounded-lg shadow hover:shadow-lg transform hover:-translate-y-1 transition">
            <a
              href="#"
              className="text-blue-600 font-medium hover:underline"
              onClick={(e) => {
                e.preventDefault();
                navigate('takeQuiz', quiz, new Array(quiz.questions.length).fill(''));
              }}
            >
              {quiz.title}
            </a>
            <div className="flex gap-4 mt-4">
              <button
                className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transform hover:-translate-y-1 transition"
                onClick={() => navigate('createQuiz', quiz)}
                aria-label={`Edit ${quiz.title}`}
              >
                Edit
              </button>
              <button
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transform hover:-translate-y-1 transition"
                onClick={() => handleDelete(quiz._id)}
                aria-label={`Delete ${quiz.title}`}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default QuizList;
